'procedure mapbits_rasterlayer (',
'    p_item   in            apex_plugin.t_item,',
'    p_plugin in            apex_plugin.t_plugin,',
'    p_param  in            apex_plugin.t_item_render_param,',
'    p_result in out nocopy apex_plugin.t_item_render_result ) is',
'    l_region_id varchar2(4000); --apex_application_page_regions.region_id%type;',
'    l_url varchar2(4000) := nvl(p_item.attribute_06, V(p_item.attribute_01));',
'    l_extent varchar2(4000) := nvl(p_item.attribute_07, V(p_item.attribute_02));',
'    l_title varchar2(4000) := nvl(p_item.attribute_09, V(p_item.attribute_03));',
'    l_checkbox_color p_item.attribute_04%type := p_item.attribute_04;',
'    l_sequence_no apex_application_page_items.display_sequence%type;',
'    l_initVisibility p_item.attribute_05%type := p_item.attribute_05;',
'    l_isTiled p_item.attribute_10%type := p_item.attribute_10;',
'    l_sourceType varchar2(12);',
'    l_error varchar2(4000) := ''''; ',
'begin',
'  begin',
'    select nvl(r.static_id, ''R'' || r.region_id), i.display_sequence into l_region_id, l_sequence_no  ',
'      from apex_application_page_items i ',
'      inner join apex_application_page_regions r on i.region_id = r.region_id ',
'      where i.item_id = p_item.id and r.source_type = ''Map'';',
'  exception',
'    when NO_DATA_FOUND then ',
'      apex_debug.message(',
'        p_message => ''ERROR: Map Layer Raster Item [%s] is not associated with a Map region.'',',
'        p0      => p_item.id,',
'        p_level   => apex_debug.c_log_level_error',
'      );',
'      l_error := l_error || ''ERROR: Map Layer Raster Item ['' ||p_item.id || ''] is not associated with a Map region.'';',
'  end;',
'  if regexp_instr(l_extent, ''^[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+$'') = 0 then',
'    --raise_application_error(-20111, ''Invalid Raster Extent ['' || l_extent || '']'');',
'    apex_debug.message(',
'        p_message => ''ERROR: Invalid Raster Extent [%s]'',',
'        p0      => l_extent,',
'        p_level   => apex_debug.c_log_level_error',
'      );',
'      l_error := l_error || ''Invalid Raster Extent ['' || l_extent || '']'';',
'',
'  end if;',
'  if p_item.attribute_10 = ''Y'' then',
'    l_sourceType := ''raster'';',
'  else',
'    l_sourceType := ''image'';',
'  end if;',
'  htp.p(''<div id="'' || p_item.name || ''" name="'' || p_item.name || ''"></div>'');',
'  apex_javascript.add_onload_code(p_code => ''apex.jQuery('' || l_region_id || '').on("spatialmapinitialized", function(){',
'    mapbits_rasterlayer("'' || p_item.name || ''", "'' || apex_plugin.get_ajax_identifier || ''", "'' || l_region_id || ''", "'' || l_title || ''", "'' || l_url || ''", "'' || l_extent || ''", '' || l_sequence_no || '', "'' || l_sourceType || ''", "'' || l_checkbox_c'
||'olor || ''", "'' || l_initVisibility || ''", "'' || l_error || ''");',
'    });'', p_key => ''MIL.ARMY.USACE.MAPBITS.LAYER.RASTER'' || p_item.name);',
'end;'))
,p_api_version=>2
,p_render_function=>'mapbits_rasterlayer'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'The Mapbits Layer Raster plugin adds support for url-based raster layers to APEX Map regions that are not managed by a web service. The layer shall appear in the map as defined by the plugin attributes and shall be togglable using the APEX Map built-'
||'in layer selector.',
'Add the plugin as an item under an APEX Map region and set the URL to point to image source.'))
,p_version_identifier=>'4.5.20230713'
,p_about_url=>'https://github.com/darklordgrep/Mapbits'
,p_plugin_comment=>wwv_flow_string.join(wwv_flow_t_varchar2(
'Module   : Mapbits 4 - Raster Layer',
'Location : $Id: item_type_plugin_mil_army_usace_mapbits_layer_raster.sql 18326 2023-07-14 13:28:57Z b2imimcf $',
'Date     : $Date: 2023-07-14 08:28:57 -0500 (Fri, 14 Jul 2023) $',
'Revision : $Revision: 18326 $',
'Requires : Application Express >= 21.1',
'',
'Version 4.5 Updates:',
'7/13/2023 Removed use of the ''render'' event to wait for a ready map. This does not appear to be necessary since the spatialmapinitialized event is already at the javascript entry point. ',
'',
'Version 4.4 Updates:',
'5/10/2023 Preventing javascript execution if the parent region is hidden.',
'',
'Version 4.3 Updates:',
'1/27/2023 To work with APEX 22.2, changed event hook to the map region''s spatialmapinitialized event in place of the page''s apexreadyend event.',
'12/19/2022 Added option to use tiled raster sources. Removed spurious js parameter and variables.',
'12/07/2022 Using render (once) event instead of the load event to load the raster. Fixed the waiting animation by adding the wait gif to the html and using jquery to remove it upon ',
'dataloading event. Changed ''WMS'' names and references to ''Raster''.',
'8/13/2022 Test with maplibre. No changes. Bumping version.',
''))
