'    l_sourceType varchar2(12);',
'    l_error varchar2(4000) := ''''; ',
'begin',
'  begin',
'    select nvl(r.static_id, ''R'' || r.region_id), i.display_sequence into l_region_id, l_sequence_no  ',
'      from apex_application_page_items i ',
'      inner join apex_application_page_regions r on i.region_id = r.region_id ',
'      where i.item_id = p_item.id and r.source_type = ''Map'';',
'  exception',
'    when NO_DATA_FOUND then ',
'      apex_debug.message(',
'        p_message => ''ERROR: Map Layer Raster Item [%s] is not associated with a Map region.'',',
'        p0      => p_item.id,',
'        p_level   => apex_debug.c_log_level_error',
'      );',
'      l_error := l_error || ''ERROR: Map Layer Raster Item ['' ||p_item.id || ''] is not associated with a Map region.'';',
'  end;',
'  if regexp_instr(l_extent, ''^[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+,[+-]?([0-9]*[.])?[0-9]+$'') = 0 then',
'    --raise_application_error(-20111, ''Invalid Raster Extent ['' || l_extent || '']'');',
'    apex_debug.message(',
'        p_message => ''ERROR: Invalid Raster Extent [%s]'',',
'        p0      => l_extent,',
'        p_level   => apex_debug.c_log_level_error',
'      );',
'      l_error := l_error || ''Invalid Raster Extent ['' || l_extent || '']'';',
'',
'  end if;',
'  if p_item.attribute_10 = ''Y'' then',
'    l_sourceType := ''raster'';',
'  else',
'    l_sourceType := ''image'';',
'  end if;',
'  htp.p(''<div id="'' || p_item.name || ''" name="'' || p_item.name || ''"></div>'');',
'  apex_javascript.add_onload_code(p_code => ''apex.jQuery('' || l_region_id || '').on("spatialmapinitialized", function(){',
'    mapbits_rasterlayer("'' || p_item.name || ''", "'' || apex_plugin.get_ajax_identifier || ''", "'' || l_region_id || ''", "'' || l_title || ''", "'' || l_url || ''", "'' || l_extent || ''", '' || l_sequence_no || '', "'' || l_sourceType || ''", "'' || l_checkbox_c'
||'olor || ''", "'' || l_initVisibility || ''", "'' || l_error || ''");',
'    });'', p_key => ''MIL.ARMY.USACE.MAPBITS.LAYER.RASTER'' || p_item.name);',
'end;'))
