'      || apex_javascript.add_attribute(''p_ajax_identifier'', apex_plugin.get_ajax_identifier)',
'      || apex_javascript.add_attribute(''p_region_id'', l_region_id)',
'      || apex_javascript.add_attribute(''p_title'', l_title)',
'      || apex_javascript.add_attribute(''p_url'', l_url)',
'      || apex_javascript.add_attribute(''p_layers'', l_layers)',
'      || apex_javascript.add_attribute(''p_sequence'', l_sequence_no)',
'      || apex_javascript.add_attribute(''p_checkbox_color'', l_checkbox_color)',
'      || apex_javascript.add_attribute(''p_initVisibility'', l_initVisibility)',
'      || apex_javascript.add_attribute(''p_tileSize'', l_tileSize)',
'      || apex_javascript.add_attribute(''p_error'', l_error)',
'    || ''});'', p_key => ''MIL.ARMY.USACE.MAPBITS.LAYER.WMS'' || p_item.name);',
'end;'))
